mapper.select:
select

from console_component
where is_deleted = 0 and cluster_id = #{clusterId}

    and component_type_code = #{type}


    and is_default = 1

mapper.update:
update console_component
set is_metadata = #{isMetadata}
where cluster_id = #{clusterId}
  and component_type_code = #{type}

mapper.select:
SELECT c.id,c.cluster_id,c.component_name,c.component_type_code,c.gmt_create,c.gmt_modified,c.is_deleted, c.hadoop_version,c.upload_file_name,c.kerberos_file_name,c.is_metadata,c.deploy_type,c.store_type FROM console_component c WHERE c.cluster_id = #{clusterId} and c.component_type_code = #{type}
and c.is_default = 1
and c.hadoop_version = #{componentVersion}
and c.deploy_type = #{deployType}
and c.is_deleted = 0 LIMIT 1

mapper.update:
update console_component set is_default = #{isDefault}
where cluster_id = #{clusterId} and is_deleted = 0 and component_type_code = #{componentType}

    and is_default = 0 order by id desc limit 1
