mapper.select:
select id,job_id,version,gmt_create,gmt_modified,is_deleted,force_cancel_flag
from schedule_job_operator_record where id > #{startId} and operator_expired < now() and is_deleted =0 and operator_type = 0
order by id asc limit 100;

select job_id as jobId
from schedule_job_operator_record
where job_id in

    #{jobId}

and is_deleted =0 and operator_type = 0;

select *
from schedule_job_operator_record where id > #{startId} and operator_type = #{type} and is_deleted =0 and node_address = #{nodeAddress}
order by id asc limit 100;

mapper.update:
UPDATE
schedule_job_operator_record
set node_address = #{nodeAddress}
where job_id IN

    #{jobId}
