mapper.select:
select * from rdos_engine_job_cache
where id > #{startId}

    and (node_address = #{nodeAddress} or node_address is null)


    and stage = #{stage}


    and job_resource = #{jobResource}

and is_deleted=0
order by id, job_priority asc
limit 500;

select * from rdos_engine_job_cache WHERE job_id in
#{jobId}
and is_deleted=0

select count(1) from rdos_engine_job_cache
where job_resource = #{jobResource} and node_address = #{nodeAddress}

    and stage in

        #{stage}


and is_deleted=0;

select max(job_priority) from rdos_engine_job_cache
where job_resource = #{jobResource} and node_address = #{nodeAddress}

    and stage = #{stage}

and is_deleted=0;

SELECT job_resource as jobResource, stage as stage, unix_timestamp(min(gmt_create)) as generateTime, count(1) as jobSize FROM rdos_engine_job_cache WHERE is_deleted = 0
and node_address = #{nodeAddress}
group by job_resource, stage order by job_resource, stage asc

SELECT * FROM rdos_engine_job_cache WHERE job_resource = #{jobResource} and stage = #{stage} and is_deleted = 0
and node_address = #{nodeAddress}
order by stage desc, job_priority asc
