mapper.sql:

    a.is_deleted = 0
    and a.dtuic_tenant_id = #{listQuery.dtuicTenantId}

        and (
        a.data_name like concat('%', #{listQuery.search} ,'%')
        or a.data_desc like concat('%', #{listQuery.search}, '%')
        )


        and a.data_type in

