            #{statusOne}



group by a.id
ORDER BY a.gmt_modified DESC

mapper.select:
select a.id as dataInfoId, a.data_name as dataName, a.data_type as dataType, a.data_version as dataVersion, a.data_desc as dataDesc, a.link_json as linkJson, a.is_meta as isMeta, a.status as status, a.gmt_modified as gmtModified from dsc_info a left join dsc_auth_ref b on a.id = b.data_info_id
ORDER BY a.gmt_modified DESC
limit #{listQuery.start}, #{listQuery.end}

select a.id as dataInfoId, a.data_name as dataName, a.data_type as dataType, a.data_version as dataVersion, a.data_desc as dataDesc, a.link_json as linkJson, a.is_meta as isMeta, a.status as status, a.gmt_modified as gmtModified from dsc_info a left join dsc_import_ref b on a.id = b.data_info_id
ORDER BY a.gmt_modified DESC
limit #{listQuery.start}, #{listQuery.end}

