mapper.select:
SELECT
FROM rdos_batch_function WHERE type=1 and is_deleted = 0 and tenant_id=-1 and project_id=-1
AND engine_type = #{engineType}

SELECT
FROM rdos_batch_function WHERE is_deleted = 0
AND type = #{functionType}
AND project_Id in (#{projectId}, -1)
AND project_Id = -1
AND engine_type = #{engineType}

SELECT name
FROM rdos_batch_function
WHERE tenant_id = #{tenantId}
  and project_id = #{projectId}
  and is_deleted = 0

    AND  engine_type = #{engineType}

select count(id)
from rdos_batch_function
where tenant_id = #{tenantId}
  and project_id = #{projectId}
  and type = #{type}
  and is_deleted = 0

SELECT

FROM rdos_batch_function
where tenant_id = #{model.tenantId} and project_id = #{model.projectId}
and type = #{model.type} and is_deleted = 0

    and modify_user_id = #{model.functionModifyUserId}


    and name like CONCAT('%',#{model.functionName},'%')


    and to_days(gmt_modified) >= to_days(#{model.startTime})


    and to_days(#{model.endTime}) >= to_days(gmt_modified)



    order by ${orderBy} ${sort}


    order by ${orderBy} desc


    limit #{start} , #{pageSize}


    limit #{pageSize}


    limit 1000

SELECT

FROM rdos_batch_function
where tenant_id = #{tenantId} and project_id = #{projectId}

    and type = #{type}


    and id in

        #{id}



    and is_deleted = #{isDeleted}

mapper.update:
UPDATE rdos_batch_function
SET is_deleted = 1, gmt_modified = now(), modify_user_id = #{userId}
WHERE project_id = #{projectId} AND is_deleted = 0;
