mapper.select:
SELECT

FROM console_account_tenant
WHERE user_id = #{userId} AND tenant_id = #{tenantId}

    AND is_deleted = #{isDeleted}


    AND account_id = #{accountId}

SELECT count(1) FROM console_account_tenant cat LEFT JOIN console_account ca ON cat.account_id = ca.id LEFT JOIN console_dtuic_tenant ct ON cat.tenant_id = ct.id LEFT JOIN console_user cu ON cu.id = cat.user_id WHERE cat.tenant_id = #{model.tenantId} AND cat.is_deleted = 0 AND ca.is_deleted = 0 AND ct.is_deleted = 0
AND ca.type = #{model.type}
AND (ca.name LIKE '%${model.name}%' OR cu.user_name LIKE '%${model.name}%')

SELECT ca.name AS name,cat.*,cu.user_name, (SELECT user_name FROM console_user WHERE id = cat.modify_user_id ) AS modifyUserName FROM console_account_tenant cat LEFT JOIN console_account ca ON cat.account_id = ca.id LEFT JOIN console_dtuic_tenant ct ON cat.tenant_id = ct.id LEFT JOIN console_user cu ON cu.id = cat.user_id WHERE cat.tenant_id = #{model.tenantId} AND cat.is_deleted = 0 AND ca.is_deleted = 0 AND ct.is_deleted = 0
AND ca.type = #{model.type}
AND (ca.name LIKE '%${model.name}%' OR cu.user_name LIKE '%${model.name}%')

select cat.*,cu.dtuic_user_id as dtuicUserId from console_account_tenant cat
                                                      left join console_user cu on cat.user_id = cu.id
                                                      join console_account ca on ca.id=cat.account_id
WHERE cat.tenant_id = #{tenantId}
  AND ca.type = #{type}
  AND cat.is_deleted = 0
