mapper.select:
select from console_component where is_deleted = 0
and engine_id in
#{engineId}

SELECT c.id,c.engine_id,c.component_name,c.component_type_code,c.gmt_create,c.gmt_modified,c.is_deleted, c.hadoop_version,c.upload_file_name,c.kerberos_file_name,c.is_metadata FROM console_component c LEFT JOIN console_engine ENGINE ON ENGINE.id = c.engine_id LEFT JOIN console_cluster cluster ON cluster.id = ENGINE.cluster_id WHERE cluster.id = #{clusterId} and c.component_type_code = #{type}
and c.is_default = 1
and c.hadoop_version = #{componentVersion}
and c.is_deleted = 0 LIMIT 1
