mapper.update:
update console_engine_tenant
set queue_id = #{queueId}
where tenant_id = #{tenantId} and engine_id = #{engineId} and is_deleted = 0

mapper.select:
select t.dt_uic_tenant_id as tenant_id,t.tenant_name,et.queue_id from console_engine_tenant et left join console_dtuic_tenant t on et.tenant_id = t.id where et.engine_id = #{engineId} and t.tenant_name like concat(concat('%',#{tenantName}),'%') and et.is_deleted = 0
order by t.${query.orderBy} ${query.sort}
order by t.${query.orderBy} desc
limit #{query.start} , #{query.pageSize}
limit #{query.pageSize}
limit 1000

select tenant_id from console_engine_tenant where is_deleted = 0
and queue_id in
#{queueId}
