mapper.select:
SELECT

FROM schedule_dict
WHERE `type` = #{type} AND is_deleted = 0 AND dict_name = #{dictName}

    AND dict_value = #{dictValue}


    AND depend_name = #{dependName}

ORDER BY sort LIMIT 1

mapper.update:
UPDATE schedule_dict
SET dict_value = #{update}
WHERE
`dict_code` = #{dictCode}
AND `dict_name` = #{dictName}
AND is_deleted = 0
AND dict_value = #{oldUpdate}

mapper.select:
SELECT

FROM schedule_dict
WHERE id > #{id}
ORDER BY id
LIMIT #{size}

SELECT

FROM schedule_dict
WHERE `dict_code` = #{dictCode} AND is_deleted = 0 AND depend_name = #{dependName} and dict_name = #{dictName}
