mapper.update:
update rdos_engine_job_cache
set node_address = #{nodeAddress}, stage = #{stage}, job_priority = #{jobPriority}, group_name = #{groupName}, gmt_modified=now()
where job_id = #{jobId}

mapper.select:
select * from rdos_engine_job_cache
where id > #{startId}

    and (node_address = #{nodeAddress} or node_address is null)


    and stage = #{stage}


    and engine_type = #{engineType}


    and group_name = #{groupName}

and is_deleted=0
order by id asc
limit 500;

select * from rdos_engine_job_cache WHERE job_id in
#{jobId}
and is_deleted=0

select count(1) from rdos_engine_job_cache
where engine_type = #{engineType} and group_name = #{groupName} and node_address = #{nodeAddress}

    and stage in

        #{stage}


and is_deleted=0;

select max(job_priority) from rdos_engine_job_cache
where  engine_type = #{engineType} and group_name = #{groupName} and node_address = #{nodeAddress}

    and stage = #{stage}

and is_deleted=0;

SELECT * FROM rdos_engine_job_cache WHERE engine_type = #{engineType} and group_name = #{groupName} and stage = #{stage} and is_deleted = 0
and node_address = #{nodeAddress}
order by stage desc, job_priority asc

SELECT engine_type as engineType, group_name as groupName, stage as stage, unix_timestamp(min(gmt_create)) as generateTime, count(1) as jobSize FROM rdos_engine_job_cache WHERE is_deleted = 0
and node_address = #{nodeAddress}
group by engine_type, group_name, stage order by engine_type, group_name, stage asc
