mapper.update:
update console_cluster_tenant
set queue_name = #{queueName}
where tenant_id = #{tenantId}
  and cluster_id = #{clusterId}
  and is_deleted = 0

mapper.select:
select t.id as tenant_id,t.tenant_name,et.queue_name,et.cluster_id,et.gmt_modified,et.gmt_create from console_cluster_tenant et left join tenant t on et.tenant_id = t.id where et.cluster_id = #{clusterId} and t.tenant_name like concat(concat('%',#{tenantName}),'%') and et.is_deleted = 0 and t.is_deleted = 0
order by t.${query.orderBy} ${query.sort}
order by t.${query.orderBy} desc
limit #{query.start} , #{query.pageSize}
limit #{query.pageSize}
limit 1000
