mapper.sql:

    a.is_deleted = 0
    and a.tenant_id = #{listQuery.tenantId}

        and (
        a.data_name like concat('%', #{listQuery.search} ,'%')
        or a.data_desc like concat('%', #{listQuery.search}, '%')
        )


        and a.data_type in

            #{dataType}



        and a.is_meta in(0,2)


        and a.status in

            #{statusOne}



group by a.id
ORDER BY a.gmt_modified DESC

