mapper.select:
SELECT

FROM rdos_batch_hive_select_sql
where job_id = #{jobId}
and tenant_id = #{tenantId}

    and is_deleted = #{isDeleted}

limit 1

mapper.delete:
delete from  rdos_batch_hive_select_sql
where id in

    #{id}

mapper.update:
UPDATE rdos_batch_hive_select_sql
SET is_deleted = 1, gmt_modified = now()
where job_id in

    #{jobId}

AND is_deleted = 0;

mapper.select:
