mapper.select:
select

from schedule_engine_job_cache
where job_id=#{jobId} and is_deleted=0 order by gmt_create desc limit 1;

SELECT

FROM schedule_engine_job_cache
WHERE job_resource = #{jobResource} and stage = #{stage} and is_deleted = 0

    and node_address = #{nodeAddress}

order by stage desc, job_priority asc
limit #{start} , #{pageSize}

SELECT job_resource as jobResource, stage as stage, unix_timestamp(min(gmt_create)) as generateTime, count(1) as jobSize FROM schedule_engine_job_cache WHERE is_deleted = 0
and node_address = #{nodeAddress}
group by job_resource, stage order by job_resource, stage asc

select

from schedule_engine_job_cache
where id > #{startId}

    and (node_address = #{nodeAddress} or node_address is null)


    and stage = #{stage}


    and job_resource = #{jobResource}

and is_deleted=0
order by id, job_priority asc
limit 500;

select min(job_priority) from schedule_engine_job_cache
where job_resource = #{jobResource} and node_address = #{nodeAddress}

    and stage in

        #{stage}


and is_deleted=0;

select count(1) from schedule_engine_job_cache
where job_resource = #{jobResource} and node_address = #{nodeAddress}

    and stage in

        #{stage}


and is_deleted=0;

mapper.update:
update schedule_engine_job_cache set node_address = #{nodeAddress}, stage = #{stage}, job_priority = #{jobPriority},
wait_reason = #{waitReason},
is_failover = 0, gmt_modified=now() where job_id = #{jobId}

mapper.delete:
delete from schedule_engine_job_cache
where job_id in

    #{jobId}

mapper.update:
UPDATE
schedule_engine_job_cache
set job_info = #{jobInfo}
WHERE job_id = #{jobId}
